import React, { useState } from "react";
import { TableRow } from "./TableRow";

export function Transacciones({socket,transacciones,sectores,usuarios,productos}){

    const [selected,setSelected] = useState('')

    function Autorizar(){
        if (selected != null && selected != '' && selected != undefined){
            const transaccion = transacciones.find(transaccion => transaccion.idtransaccion_pen == selected)
            socket.emit('autorizarPen',transaccion)
        }else{
            alert('Selecciona una Transaccion')
        }
    }

    return <main>
        <h1 className="titulo">Transacciones Pendientes</h1>
    <div className="OptionsPen">
        <button onClick={()=>{
            Autorizar()
        }}>Autorizar</button>
    </div>
    <table className="TablePend">
      <thead>
        <tr>
          <th>ID Transacci√≥n</th>
          <th>Usuario</th>
          <th>Sector Origen</th>
          <th>Sector Destino</th>
          <th>Producto</th>
          <th>Cantidad</th>
          <th>Fecha</th>
        </tr>
      </thead>
      <tbody>
        {
            transacciones.map(tran => {
                const user = usuarios.find(user => user.idusuario == tran.idusuario)
                const sectorO = sectores.find(sector => sector.idsector == tran.idsector_o)
                const sectorD = sectores.find(sector => sector.idsector == tran.idsector_d)
                const producto = productos.find(producto => producto.idproducto == tran.idproducto)
                const cantidad = tran.cantidad
                const fecha = tran.fecha
                return <TableRow tran={tran} user={user} sectorO={sectorO} sectorD={sectorD} producto={producto} cantidad={cantidad} fecha={fecha} selected={selected} setSelected={setSelected}/>
            })
        }
      </tbody>
    </table>
    </main>
}
