import React, { useState } from "react";
import { ProductoItem } from "./ProductoItem";

export function AdministrarProductos({socket,productos,sectores}){

    const [productoSelected , setProductoSelected] = useState('')
    const [modalCrear,setModalCrear] = useState('inactive')
    const [modalEditar,setModalEditar] = useState('inactive')

    const [productoCrear , setProductoCrear] = useState('')
    const [sectorCrear , setSectorCrear] = useState('')
    const [productoEditar , setProductoEditar] = useState('')
    const [sectorEditar , setSectorEditar] = useState('')

    return <main>
        <h1 className="titulo">Administrar Productos</h1>
        <div className="options">
            <div className="option">Crear Producto</div>
            <div className="option" onClick={()=>{
                if(productoSelected != '' && productoSelected != undefined){

                }else{
                    alert('Seleccionar Producto')
                }
            }}>Editar Producto</div>
            <div className="option" onClick={()=>{
                if(productoSelected != '' && productoSelected != undefined){

                }else{
                    alert('Seleccionar Producto')
                }
            }}>Eliminar Producto</div>
        </div>
        <div className={modalCrear}>
            <div className="modal-content">
                <div className="modal-form" action="">
                    <button className="close" onClick={()=>{
                        setModalCrear('inactive')
                    }}>X</button>
                    <label htmlFor="productos">Producto a transformar</label>
                    <input type="text" name="productos" id="productos" onChange={(e)=>{
                        setProductoCrear(e.target.value)}}/>
                    <label htmlFor="sector">Sector</label>
                    <select name="sector" id="sector">
                        {
                            sectores.map(sector =>{
                                return <option value={sector.idsector}>{sector.nombre}</option>
                            })
                        }
                    </select>
                    <button className="submit" onClick={()=>{
                        const data = {
                            idproducto:crearProd,
                            agricoin:crearAgricoin
                        }
                        socket.emit('crearConversion',data)
                        setCrearAgriCoinDefault('')
                        setCrearProdDefault(productos[0]?.idproducto)
                        setModalCrear('inactive')
                    }}>Crear</button>
                </div>
            </div>
        </div>
        <div className={modalEditar}>
            <div className="modal-content">
                <div className="modal-form" action="">
                    <button className="close" onClick={()=>{
                        setModalEditar('inactive')
                    }}>X</button>
                    <label htmlFor="productos">Producto a transformar</label>
                    <select value={prodSelected} name="productos" id="productos" onChange={(e)=>{
                        setEditarProd(e.target.value)
                    }}>
                        {
                            productos.map(producto =>{
                                return <option value={producto.idproducto}>{producto.nombre}</option>
                            })
                            
                        }
                    </select>
                    <label htmlFor="cant">Equivalencia en AgriCoins</label>
                    <input onChange={(e)=>{
                        setEditarAgricoin(e.target.value)
                    }} defaultValue={agricoinSelected} type="number" name="cant" id="cant" placeholder="Cantidad de agricoins" />
                    <button onClick={()=>{
                        const data = {
                            id:converSelected,
                            idproducto:editarProd,
                            agricoin:editarAgricoin
                        }
                        socket.emit('updateConversion',data)
                        setModalEditar('inactive')
                        setConverSelected('')
                    }} className="submit">Editar</button>
                </div>
            </div>
        </div>
        <table className="table-conversiones">
            <thead>
                <tr>
                    <th>Producto</th>
                    <th>Sector</th>
                </tr>
            </thead>
            <tbody>
                {
                    productos.map(producto =>{
                        const sector = sectores.find(sector => sector.idsector == producto.idsector)
                        return <ProductoItem sector={sector} producto={producto} setProductoSelected={setProductoSelected} productoSelected={productoSelected}/>
                    })
                }
            </tbody>
        </table>
    </main>
}
