import React, { useEffect, useState } from "react";

export function ConverRow({converSelected,setConverSelected,producto,agricoin}){
    const [style,setStyle] = useState('')

    useEffect(()=>{
        if(converSelected == producto.idproducto){
            setStyle('selected')
        }else{
            setStyle('')
        }
    },[converSelected])

    return <tr onClick={()=>{
        setConverSelected(producto.idproducto)
    }} className={style}>
        <td>{producto?.nombre}</td>
        <td>{agricoin}</td>
    </tr>
}
