import React, { useState } from "react";

export function TableRow({tran,user,sectorO,sectorD,producto,cantidad,fecha,selected,setSelected}){

    const [style,setStyle] = useState("selectionTr")

    return <tr className={style} key={tran.idtransaccion_pen} onClick={()=>{
        if (selected != tran.idtransaccion_pen){
            setSelected(tran.idtransaccion_pen)
            setStyle('selectionTrSelected')
        }else{
            setSelected('')
            setStyle('selectionTr')
        }
    }}>
    <td>{tran.idtransaccion_pen}</td>
    <td>{user?.apenom}</td>
    <td>{sectorO?.nombre}</td>
    <td>{sectorD?.nombre}</td>
    <td>{producto?.nombre}</td>
    <td>{cantidad}</td>
    <td>{fecha}</td>
  </tr>
}
