import React, { useState } from "react";
import { SectorItem } from "./SectorItem";
import Escuela from '../Img/escuela.jpg'

export function AdministrarSectores({sectores,socket}){

    const [sectorSelected , setSectorSelected] = useState('')
    const [modalCrear,setModalCrear] = useState('')
    const [modalEditar,setModalEditar] = useState('')

    return <main>
        <h1 className="titulo">Administrar Sectores</h1>
        <div className="options">
            <div className="option">Crear Sector</div>
            <div className="option">Editar Sector</div>
            <div className="option">Eliminar Sector</div>
        </div>
        <div className="modals-container">
            <div className="modal-contenedor">
                <h1>Crear Sector</h1>
                <div>
                    <img src={Escuela} alt="" />
                </div>
                <form>
                    <input type="text" placeholder="Nombre del Sector" />
                    <button>CREAR</button>
                </form>
            </div>
        </div>
        <div className="grid-container">
            {
                sectores.map(sector =>{
                    return <SectorItem sector={sector} sectorSelected={sectorSelected} setSectorSelected={setSectorSelected}/>
                })
            }
        </div>
    </main>
}
