import { Header } from "./components/Header";
import './style/app.css'
import { Menu } from "./components/Menu"; 
import { BrowserRouter, Route, Routes } from "react-router-dom";
import io from 'socket.io-client'
import { CrearCuenta } from "./components/CrearCuenta";
import { useEffect, useState } from "react";
import { Login } from "./components/Login";
import { CrearSector } from "./components/CrearSector";
import { CrearCargo } from "./components/CrearCargo";
import { CrearProducto } from "./components/CrearProducto";
import { TransaccionesPen } from "./components/TransaccionesPen";
import { Transacciones } from "./components/Transacciones";

function App() {

  const socket = io('http://192.168.1.53:5000')
  const [usuarios , setUsuarios] = useState([])
  const [usuario , setUsuario] = useState('')

  const [sectores,setSectores] = useState([])
  const [cargos,setCargos] = useState([])
  const [productos,setProductos] = useState([])
  const [transacciones,setTransacciones] = useState([])
  const [transaccionesPen,setTransaccionesPen] = useState([])

  useEffect(()=>{
    GetUser()
    GetSectores()
    GetProductos()
    GetCargos()
    GetTransacciones()
    GetTransaccionesPen()

    socket.on('Cambios',()=>{
      GetUser()
      GetSectores()
      GetProductos()
      GetCargos()
      GetTransacciones()
      GetTransaccionesPen()
    })

  },[])

  function GetUser(){
    socket.emit('getUsuarios')
    socket.on('putUsuarios',(usuarios)=>{
      setUsuarios(usuarios)
      console.log(usuarios)
    })
  }

  function GetProductos(){
    socket.emit('getProductos')
    socket.on('putProductos',(productos)=>{
      setProductos(productos)
      // console.log(productos)
    })
  }

  function GetTransacciones(){
    socket.emit('getTransacciones')
    socket.on('putTransacciones',(Transacciones)=>{
      setTransacciones(Transacciones)
      // console.log(Transacciones)
    })
  }

  function GetTransaccionesPen(){
    socket.emit('getTransaccionesPen')
    socket.on('putTransaccionesPen',(TransaccionesPen)=>{
      setTransaccionesPen(TransaccionesPen)
      console.log(TransaccionesPen)
    })
  }

  function GetSectores(){
    socket.emit('getSectores')
    socket.on('putSectores',(sectores)=>{
      setSectores(sectores)
      // console.log(sectores)
    })
  }

  function GetCargos(){
    socket.emit('getCargos')
    socket.on('putCargos',(cargos)=>{
      setCargos(cargos)
      console.log(cargos)
    })
  }

  return (
    <BrowserRouter>
      <Header usuario={usuario}/>
      <Routes>
        <Route path="/" element={<Login usuarios={usuarios} setUsuario={setUsuario}/>}/>
        <Route path="/Menu/:idusuario" element={<Menu usuarios={usuarios} setUsuario={setUsuario}/>}/>
        <Route path="/CrearCuenta" element={<CrearCuenta socket={socket}/>}/>
        <Route path="/CrearSector" element={<CrearSector socket={socket}/>}/>
        <Route path="/CrearCargo" element={<CrearCargo socket={socket}/>}/>
        <Route path="/CrearProducto" element={<CrearProducto socket={socket} sectores={sectores}/>}/>
        <Route path="/TransaccionesPen" element={<TransaccionesPen socket={socket} transacciones_pen={transaccionesPen} usuarios={usuarios} sectores={sectores} productos={productos}/>}/>
        <Route path="/Transacciones" element={<Transacciones socket={socket} transacciones={transacciones} usuarios={usuarios} sectores={sectores} productos={productos}/>}/>
      </Routes>
    </BrowserRouter>
  );
}


export default App;
